var modal=document.querySelector(".modal"),open=document.querySelectorAll(".modal-open");for(i=0;i<open.length;++i)open[i].addEventListener("click",function(){modal.classList.contains("modal--closed")&&(modal.classList.remove("modal--closed"),modal.classList.add("modal--opened"))});window.onclick=function(o){o.target==modal&&(modal.classList.remove("modal--opened"),modal.classList.add("modal--closed"))},window.addEventListener("keydown",function(o){27===o.keyCode&&modal.classList.contains("modal--opened")&&(o.preventDefault(),modal.classList.remove("modal--opened"),modal.classList.add("modal--closed"))});